import React from 'react';

type InfoboxProps = {
    Title: string;
    Description: string;
    Image?: string;
    BackgroundColor?: string 
    TextColor?: string 
};
const TitleStyle: React.CSSProperties = {
    color: '#122701',
    fontFamily: 'Baloo Bhai 2',
    fontSize: '14px',
    fontStyle: 'normal',
    fontWeight: 700,
    lineHeight: '16px',
    letterSpacing: '0.14px',
    textTransform: 'uppercase',
};



const Infobox: React.FC<InfoboxProps> = ({ Title, Description, Image, BackgroundColor, TextColor}) => {
    const DescriptionStyle: React.CSSProperties = {
        fontFamily: 'Inter',
        fontSize: '8px',
        fontStyle: 'normal',
        fontWeight: 400,
        lineHeight: '9px', 
        flex:'1 0 0',
        color: TextColor || '#122701'

    };
    return (
        <div 
        style={{
            backgroundColor: BackgroundColor || '#F4FFEB', 
            display: 'flex',
            flexDirection: 'column',
            padding: '12px 0px 13px 0px',
            justifyContent: 'center',
            alignItems: 'center', 
            borderRadius: '10px',
            boxShadow: '0px 4px 4px 0px rgba(0, 0, 0, 0.25), 0px 4px 4px 0px rgba(0, 0, 0, 0.25), 0px 4px 4px 0px rgba(0, 0, 0, 0.25)',

        }}>
            <h1 style={TitleStyle }>{Title}</h1>
            <div
            style={{
                display: 'flex',
                justifyContent: 'flex-end',
                alignItems: 'center', 
                gap: '10px', 
                height: ''
            }}
            >
                <p style={DescriptionStyle}>{Description}</p>
                {Image && 
                <img src={Image} alt={`${Title} Image`}  style={{
                    height: '50%', 
                    objectFit: 'contain'


                    }
                }
                />}
            </div>

        </div>
    );
};

export default Infobox;
