import React, { useState } from 'react';
type ControlButtonsProps = {
   iconLink: string 
   iconTitle: string 
};
const dynamicStyles = (isIconOn: boolean) : React.CSSProperties => {
    return {
      backgroundColor: isIconOn ? '#122701' : '#315B0F',
      objectFit:'fill'

    };
};

const ControlButton : React.FC<ControlButtonsProps> = ({iconTitle, iconLink }) => {
    const [isIconOn, setIsIconOn] = useState(false);
    const IconStyles = dynamicStyles(isIconOn);


    const handleToggle = () => {
      setIsIconOn(!isIconOn);
    };

    return (
      <div>
        <button onClick={handleToggle}style={{ borderRadius: '50%', border: 'none', padding: '0' }} > 
        <img src={iconLink} alt={`${iconTitle} icon`} style={IconStyles} />
        </button>
      </div>
    );
  };


export default ControlButton
