import React from 'react';
import Infobox from '../Components/Infobox';
import Vents from '../Components/Vent';
import Dail from '../Components/Dial';
import ControlButton from '../Components/ControlButton';
import lighteningBolt from '../Icons/LightneingBolt/Vector 2@2x.png'
import iconlink from '../Icons/shaver.svg'

//fake info cards 
const DEMO_TITLE= 'Title Area'
const DEMO_DESCRIPTION= 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Convallis convallis tellus id interdum.'
const DEMO_IMAGE= lighteningBolt

//fake buttons
const DEMO_ICON_TITLE = 'Demo Icon Link'
const DEMO_ICON_LINK = iconlink

type TitleBoxProps = {
    SectionTitle: string;
};

const TitleDesign = {
    color: '#122701',
    fontFamily: 'Inter',
    fontSize: '8px',
    fontStyle: 'normal',
    fontWeight: 600,
    lineHeight: '9px',

};

function TitleBox({ SectionTitle }: TitleBoxProps) {
    const uppercasedTitle = SectionTitle.toUpperCase();
    return (
        <div>
            <h1 style={TitleDesign}>
                {uppercasedTitle}
            </h1>
        </div>
    );
}


function Main() {
    return (
        <>
            <div className='Info-area' style={{
                display: 'flex',
                flexDirection: 'row',
                paddingBottom: '30px',
                alignItems: "center",
                justifyContent: 'center',
                gap: '25px'
            }}>
                <Infobox Title={DEMO_TITLE} Description={DEMO_DESCRIPTION}  />
                <Infobox Title={DEMO_TITLE} Description={DEMO_DESCRIPTION} Image={DEMO_IMAGE}
                BackgroundColor= '#315B0F' TextColor='#F4FFEB'
                />
            </div>
            <TitleBox SectionTitle='Vents' />
            <Vents leftOnePercent={10} leftTwoPercent={10} rightOnePercent={30} rightTwoPercent={50} />
            <TitleBox SectionTitle='Temps' />
            <Dail currentTemp={76} setTemp={72}/>
            <TitleBox SectionTitle='Controls' />
            <div className='control-button-area'>
                <ControlButton iconLink={DEMO_ICON_TITLE} iconTitle={DEMO_ICON_LINK}/>
                <ControlButton iconLink={DEMO_ICON_TITLE} iconTitle={DEMO_ICON_LINK}/>
                <ControlButton iconLink={DEMO_ICON_TITLE} iconTitle={DEMO_ICON_LINK}/>
                <ControlButton iconLink={DEMO_ICON_TITLE} iconTitle={DEMO_ICON_LINK}/>
            </div>

        </>
    );
}

export default Main;
