import React from 'react';

type InfoboxProps = {
    Title: string;
    Description: string;
    Image?: string;
    BackgroundColor?: string 
    TextColor?: string 
};
const TitleStyle: React.CSSProperties = {
    color: '#122701',
    fontFamily: 'Baloo Bhai 2',
    fontSize: '14px',
    fontStyle: 'normal',
    fontWeight: 700,
    lineHeight: '16px',
    letterSpacing: '0.14px',
    textTransform: 'uppercase',
};



const Infobox: React.FC<InfoboxProps> = ({ Title, Description, Image, BackgroundColor, TextColor}) => {
    const DescriptionStyle: React.CSSProperties = {
        fontFamily: 'Inter',
        fontSize: '8px',
        fontStyle: 'normal',
        fontWeight: 400,
        lineHeight: '9px', 
        flex:'1 0 0',
        color: TextColor || '#122701'

    };
    return (
        <div>
            <div>
                <h1 style={TitleStyle }>{Title}</h1>
                <p style={DescriptionStyle}>{Description}</p>
            </div>
            {Image && <img src={Image} alt={`${Title} Image`} />}
        </div>
    );
};

export default Infobox;
