import React, { useState } from 'react';
type ControlButtonsProps = {
   iconLink: string 
   iconTitle: string 
};
const dynamicStyles = (isIconOn: boolean) => {
    return {
      backgroundColor: isIconOn ? 'green' : 'red',
      color: isIconOn ? 'white' : 'black',
    };
};

const ControlButton : React.FC<ControlButtonsProps> = ({iconTitle, iconLink }) => {
    const [isIconOn, setIsIconOn] = useState(false);
    const IconStyles = dynamicStyles(isIconOn);


    const handleToggle = () => {
      setIsIconOn(!isIconOn);
    };

    return (
      <div>
        <button onClick={handleToggle} > 
        <img src={iconLink} alt={`${iconTitle} icon`} style={IconStyles} />
        </button>
      </div>
    );
  };


export default ControlButton
